  //     .then((data)=> setdataList (data))     

  //   } catch (error) {
  //     console.log(error)
  //   }
  }

  useEffect(() => {
    getFirms();
    console.log(dataList); 
    console.log(brand);

  }, [ ]);




  return (
    <>
    <div>
      <p> Model you choose: </p> 
      <Dropdown>
      <DropdownButton id="dropdown-basic-button" title="Brands" drop="down">
        {dataList?.map((item,index)=> {
          const {sira, sirket_adi,ulke,telefon_numarasi,adres,araba_modelleri}=item;
          return (
            <div>
            <Dropdown.Item key={sirket_adi} onClick={(e)=> setbrand(e.value)}>{sirket_adi}</Dropdown.Item>
            </div> 

          )
        })}

    </DropdownButton>
    </Dropdown>


    </div>
    </>
  )

}

export default Firms
